const initialState={
    product:[],
    product_copy: [],
    category:[],
    productosFiltrados: []
}


function rootReducer(state = initialState, action){
    switch (action.type) {
        case "GET_PRODUCT" :
            return{
                ...state,
                product: action.payload,
                productosFiltrados: action.payload 
            }
            
        case "FILTRO":
            const products = state.product_copy
            const filtro = action.payload === '' ? products : products.filter(p => {
                if(p.category === action.payload){
                    return p
                }
            })
            return{
                ...state,
                products: filtro
            }
        case "ordenAZ":
            if (action.payload === 'A-Z' || action.payload === 'Z-A') {
                let n = 0;
                const ordenV = (action.payload === 'A-Z')
                    ?
                    (state.productosFiltrados.sort((a, b) => { return (((a.name.trim() > b.name.trim()) ? 1 : (a.name.trim() < b.name.trim()) ? -1 : 0)) }))
                    :
                    (state.productosFiltrados.sort((a, b) => { return ((a.name.trim() < b.name.trim()) ? 1 : ((a.name.trim() > b.name.trim()) ? -1 : 0)) }))
                //es necesario realizar un cambio, para que me muestre la lista ordenada al renderizar la página, agrego una nueva propiedad idd 
                const ordenVCid = ordenV.map((e) => { return ({ ...e, idd: n++ }) })
                return {
                    ...state,
                    productosFiltrados: ordenVCid
                }
            }
        case "busqueda":
                let filtroPorNombre = state.productosFiltrados.filter((e) => {
                    return (e.name.toUpperCase().includes(action.payload.toUpperCase()))
                });
    
                return {
                    ...state,
                    productosFiltrados: filtroPorNombre,
                    searchName: action.payload,
                }
        case "POST_PRODUCT":
                return{
                    ...state
                }
    
        default:
            return state
    }

}

export default rootReducer;
